mapper.sql:
from community_post cp left outer JOIN community_profile cpro ON cp.comu_profile_no = cpro.comu_profile_no LEFT outer JOIN board_type bt ON cp.board_type_code = bt.board_type_code LEFT outer JOIN (SELECT comu_post_no, COUNT(*) AS like_count FROM community_like GROUP BY comu_post_no) cl_count ON cp.comu_post_no = cl_count.comu_post_no left outer join (select COMU_POST_NO, count(comu_reply_no) as reply_count from community_reply where comu_reply_del_yn = 'N' group by COMU_POST_NO) rc on cp.comu_post_no = rc.comu_post_no left outer join (select cc.comu_like_no as yn, cc.comu_post_no from community_like cc where cc.comu_profile_no = #{myComuProfileNo}) lk on cp.comu_post_no = lk.comu_post_no WHERE cp.art_group_no = #{artGroupNo} and cp.comu_post_del_yn = 'N'
and cp.comu_post_content like '%'||#{searchWord}||'%'
and cpro.MEM_USERNAME like '%'||#{searchType}||'%'
and cp.board_type_code = #{boardTypeCode}
and cp.board_type_code = #{boardTypeCode}

mapper.select:
SELECT ag.art_group_no , ag.art_group_type_code , ag.emp_username , ag.art_group_debutdate , ag.art_group_nm , ag.art_group_content , ag.art_group_reg_date , ag.art_group_del_yn , ag.art_group_profile_img , ag.art_group_mod_date
, tf.totalfan - ta.totalArtist as total_fan , ta.totalArtist as total_artist
, a.ART_NO , a.MEM_USERNAME , a.ART_NM , a.ART_CONTENT , a.ART_REG_DATE , a.ART_MOD_DATE , a.ART_PROFILE_IMG , a.ART_DEBUTDATE , a.ART_DEL_YN FROM artist_group ag left outer join (select art_group_no , count(comu_profile_no)as totalFan , comu_del_yn from community_profile group by art_group_no, comu_del_yn) tf on ag.art_group_no = tf.art_group_no and tf.comu_del_yn = 'N' left outer join (select art_group_no , count(ART_NO) as totalArtist from artist_group_map group by art_group_no) ta on ag.art_group_no = ta.art_group_no left outer join artist_group_map am on ag.art_group_no = am.art_group_no left outer join artist a on a.art_no = am.art_no and a.art_del_yn = 'N' where ag.art_group_no = #{artGroupNo}

mapper.insert:

