mapper.update:
update community_post
set
	  file_group_no = #{fileGroupNo}
	, comu_post_content = #{comuPostContent}
	, comu_post_mod_date = sysdate
	, comu_post_mbsp_yn = #{comuPostMbspYn}
where
	comu_post_no = #{comuPostNo}

update community_post
set
	comu_post_del_yn = #{comuPostDelYn}
where
	comu_post_no = #{comuPostNo}

