mapper.select:
select b.file_group_no, b.goods_no, b.goods_nm, b.rnum
from (
	select a.file_group_no,a.goods_no, a.goods_nm, rownum rnum
	from(
		select 
		    file_group_no,
		    goods_no,
		    goods_nm
		from goods
		where art_group_no = #{artGroupNo}
		and goods_div_code = 'GDC003'
		order by GOODS_REG_DATE desc
		)a
	)b

where rnum >= 1 and rnum <=3

